import React from "react";
import TableLine from "./TableLine";
import { useState, useContext } from "react";
// import data from "../../../src/words.json";
import { WordsContext } from "../App/Words";

export default function Table() {
  const { words, updateWord } = useContext(WordsContext);

  const handleWordChange = (editedWord) => {
    setWords(
      words.map((word) => {
        if (word.id === editedWord.id) {
          return editedWord;
        } else {
          return word;
        }
      })
    );
  };
  return (
    <div className="table container">
      <table>
        <thead>
          <tr>
            <th>English</th>
            <th>Transcription</th>
            <th>Russian</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {words.map((word) => (
            <TableLine
              key={word.id}
              word={word}
              onChange={handleWordChange}
              onDelete={(wordId) => {
                updateWord(words.filter((word) => word.id !== wordId));
              }}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
}
