import React from "react";
import TableLine from "./TableLine";
import { useState, useContext } from "react";
// import data from "../../../src/words.json";
import { WordsContext } from "../App/Words";

export default function Table() {
  const { words, updateWord, deleteWord, addWord } = useContext(WordsContext);

  const [isClosed, setClosed] = useState(true);

  //инпуты
  const [ruValue, setRuValue] = useState("Add russian word");
  const [trValue, setTrValue] = useState("Add transcription");
  const [engValue, setEngValue] = useState("Add english word");

  //слова

  // const [ruWord, setRuWord] = useState("");
  // const [transcription, setTranscription] = useState("");
  // const [engWord, setEngWord] = useState("");

  function addNewWord() {
    setRuWord(ruValue);
    setEngWord(engValue);
    setTranscription(trValue);
  }
  addWord();
  return (
    <div className="table container">
      <div className="new_word">
        <button onClick={() => setClosed(!isClosed)}>Add new word</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>English</th>
            <th>Transcription</th>
            <th>Russian</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr className={isClosed ? "disabled" : ""}>
            <td>
              <input
                type="text"
                placeholder={engValue}
                onChange={(e) => setEngValue(e.target.value)}
              />
            </td>
            <td>
              <input
                type="text"
                placeholder={trValue}
                onChange={(e) => setTrValue(e.target.value)}
              />
            </td>
            <td>
              <input
                type="text"
                placeholder={ruValue}
                onChange={(e) => setRuValue(e.target.value)}
              />
            </td>
            <td>
              <button onClick={addNewWord}>Save</button>
            </td>
          </tr>
          {words.map((word) => (
            <TableLine
              key={word.id}
              word={word}
              onChange={updateWord}
              onDelete={deleteWord}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
}
