import React from "react";
import "../../style/Table.scss";
import { useState } from "react";

export default function TableLine(props) {
  const [engWord, setEngWord] = useState(props.word.english);
  const [ruWord, setRuWord] = useState(props.word.russian);
  // const [inputState, setInputState] = useState("disabled");
  // const [pState, setpState] = useState("enabled");
  const [inputValue, setInputValue] = useState(engWord);
  const [inputRu, setInputRu] = useState(ruWord);

  const [inputState, setInputState] = useState(true);

  function edit() {
    // setInputState(inputState === "disabled" ? "enabled" : "disabled");
    // setpState(pState === "enabled" ? "disabled" : "enabled");

    setInputState(!inputState);
    setEngWord(inputValue);
    setRuWord(inputRu);
    props.onChange({
      ...props.word,
      english: inputValue,
      russian: inputRu,
    });
  }

  return (
    <tbody>
      <tr>
        <td>
          <input
            onChange={(e) => setInputValue(e.target.value)}
            className={inputState ? "disabled" : ""}
            value={inputValue}
            type="text"
            placeholder={engWord}
          />
          {/* <button className={inputState} onClick={editEngWord}>
            Save
          </button> */}
          <span className={inputState ? "" : "disabled"} id={props.id}>
            {engWord}
          </span>
        </td>
        <td>
          <span>{props.word.transcription}</span>
        </td>
        <td>
          <input
            onChange={(e) => setInputRu(e.target.value)}
            className={inputState ? "disabled" : ""}
            value={inputRu}
            type="text"
            placeholder={ruWord}
          />
          {/* <button className={inputState} onClick={editRuWord}>
            Save
          </button> */}
          <span className={inputState ? "" : "disabled"} id={props.id}>
            {ruWord}
          </span>
        </td>
        <td className="tableBtn">
          <button onClick={edit}>{inputState ? "Edit" : "Save"} </button>
          <button onClick={() => props.onDelete(props.word.id)}>Delete</button>
        </td>
      </tr>
    </tbody>
  );
}
