import React from "react";
import "../../style/Table.scss";
import { useState } from "react";

export default function Table(props) {
  const [engWord, setEngWord] = useState(props.english);
  const [ruWord, setRuWord] = useState(props.russian);
  const [words, setWords] = useState(props.words);
  const [inputState, setInputState] = useState("disabled");

  function edit() {
    console.log(words);
    setInputState("enabled");
  }

  return (
    <tbody>
      <tr>
        <td>
          <input className={inputState} type="text" placeholder={engWord} />
          <button className={inputState}>Save</button>
          {engWord}
        </td>
        <td>{props.transcription}</td>
        <td>{ruWord}</td>
        <td className="tableBtn">
          <button onClick={edit}>Edit</button>
          <button>Delete</button>
        </td>
      </tr>
    </tbody>
  );
}
