import React from "react";
import "../../style/Table.scss";
import { useState } from "react";

export default function Table(props) {
  // props.word
  // props.onChange
  // props.onDelete

  const [engWord, setEngWord] = useState(props.word.english);
  const [ruWord, setRuWord] = useState(props.word.russian);
  const [inputState, setInputState] = useState("disabled");
  const [pState, setpState] = useState("enabled");
  const [inputValue, setInputValue] = useState(engWord);

  console.log("engword", props.allWords);

  function edit() {
    setInputState(inputState === "disabled" ? "enabled" : "disabled");
    setpState(pState === "enabled" ? "disabled" : "enabled");
  }

  function editEngWord() {
    setEngWord(inputValue);

    props.onChange({
      ...props.word,
      english: inputValue,
    });

    setInputState(inputState === "disabled" ? "enabled" : "disabled");
    setpState(pState === "enabled" ? "disabled" : "enabled");
  }

  return (
    <tbody>
      <tr>
        <td>
          <input
            onChange={(e) => setInputValue(e.target.value)}
            className={inputState}
            value={inputValue}
            type="text"
            placeholder={engWord}
          />
          <button className={inputState} onClick={editEngWord}>
            Save
          </button>
          <span className={pState} id={props.id}>
            {" "}
            {engWord}
          </span>
        </td>
        <td>{props.word.transcription}</td>
        <td>{ruWord}</td>
        <td className="tableBtn">
          <button onClick={edit}>Edit</button>
          <button onClick={() => props.onDelete(props.word.id)}>Delete</button>
        </td>
      </tr>
    </tbody>
  );
}
