import React from "react";
import "../../style/Table.scss";
import { useState } from "react";

export default function Table(props) {
  const [engWord, setEngWord] = useState(props.english);
  const [ruWord, setRuWord] = useState(props.russian);
  const [words, setWords] = useState(props.words);
  const [inputState, setInputState] = useState("disabled");
  const [pState, setpState] = useState("enabled");
  const [inputValue, setInputValue] = useState(engWord);

  function edit() {
    console.log(words);
    setInputState(inputState === "disabled" ? "enabled" : "disabled");
    setpState(pState === "enabled" ? "disabled" : "enabled");
  }

  return (
    <tbody>
      <tr>
        <td>
          <input
            onChange={(e) => setInputValue(e.target.value)}
            className={inputState}
            value={inputValue}
            type="text"
            placeholder={engWord}
          />
          <button className={inputState}>Save</button>
          <span className={pState}> {engWord}</span>
        </td>
        <td>{props.transcription}</td>
        <td>{ruWord}</td>
        <td className="tableBtn">
          <button onClick={edit}>Edit</button>
          <button>Delete</button>
        </td>
      </tr>
    </tbody>
  );
}
